// LLM response string
let raw = $json["text"] || "";

// Clean whitespace
raw = raw.trim();

// Remove ```json fences if present
if (raw.startsWith("```")) {
  raw = raw
    .replace(/^```json/, "")
    .replace(/^```/, "")
    .replace(/```$/, "")
    .trim();
}

let parsed;
try {
  parsed = JSON.parse(raw);
} catch (err) {
  // If parsing fails, return the raw text as finalresult
  parsed = {
    text: raw,
    title: ""
  };
}

// Return normalized keys (always provide defaults)
return {
  title: parsed.title || "",
  text: parsed.text || ""
};

// Inputs from webhook or previous node
const toneInput = $('Webhook').first().json.body.tone;
const useCaseInput = $('Webhook').first().json.body.useCase;
const styleInput = $('Webhook').first().json.body.translationStyle;

const records = $input.all();

// Helper to find record by matching its Name (case-insensitive)
const findRecord = (name) => {
  if (!name) return null;
  return records.find(r =>
    r.json.Name.toLowerCase().trim() === name.toLowerCase().trim()
  );
};

// Extract each one
const tone = findRecord(toneInput);
const useCase = findRecord(useCaseInput);
const style = findRecord(styleInput);
const brand = records.find(r =>
  r.json.Name.toLowerCase().includes("brand")
);

// Return unified output
return [
  {
    json: {
      brand_name: brand?.json.Name || "",
      brand_instructions: brand?.json.Instructions || "",

      tone_name: tone?.json.Name || "",
      tone_instructions: tone?.json.Instructions || "",

      style_name: style?.json.Name || "",
      style_instructions: style?.json.Instructions || "",

      usecase_name: useCase?.json.Name || "",
      usecase_instructions: useCase?.json.Instructions || ""
    }
  }
];
