// LLM response string
let raw = $json["text"] || "";

// Clean whitespace
raw = raw.trim();

// Remove ```json fences if present
if (raw.startsWith("```")) {
  raw = raw
    .replace(/^```json/, "")
    .replace(/^```/, "")
    .replace(/```$/, "")
    .trim();
}

let parsed;
try {
  parsed = JSON.parse(raw);
} catch (err) {
  // If parsing fails, return the raw text as finalresult
  parsed = {
    finalresult: raw,
    explanation: "",
    title: ""
  };
}

// Return normalized keys (always provide defaults)
return {
  title: parsed.title || "",
  finalOutput: parsed.finalresult || "",
  explanation: parsed.explanation || ""
};

