// Get targetLang from incoming JSON
const lang = $('Webhook').first().json.body.targetLang;

// Define mapping
const map = {
  "English-UK": "EN-GB",
  "English-US": "EN-US",
  "Spanish": "ES",
  "German": "DE",
  "French": "FR",
  "Italian": "IT",
  "Dutch": "NL"
};

// If no match, fallback to English-US
const deeplCode = map[lang] || "EN-US";

// Return the updated object
return {
  ...$json,
  targetLangCode: deeplCode
};

